import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const [previousValue, setPreviousValue] = useState(56000);
  const [currentValue, setCurrentValue] = useState(56000);
  const [rectangles, setRectangles] = useState([]);
  const [canvasHeight, setCanvasHeight] = useState(300);
  const [minValue, setMinValue] = useState(55655);
  const [maxValue, setMaxValue] = useState(56345);
  const canvasRef = useRef(null);

  const generateValue = () => {
    const newValue = Math.floor(Math.random() * (56345 - 55655 + 1)) + 55655;
    setCurrentValue(newValue);
  };

  useEffect(() => {
    if (currentValue !== previousValue) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      const scale = window.devicePixelRatio || 1;
      canvas.width = 500 * scale;
      canvas.height = canvasHeight * scale;
      ctx.scale(scale, scale);

      const difference = currentValue - previousValue;
      const step = difference > 0 ? 1 : -1;
      let progress = 0;

      // Compute new limits and adjust canvas height if needed
      const newMinValue = Math.min(minValue, currentValue - 345);
      const newMaxValue = Math.max(maxValue, currentValue + 345);
      if (currentValue < minValue) {
        const newHeight = canvasHeight + 100; // Add extra space below
        setCanvasHeight(newHeight);
        setMinValue(newMinValue);
      }
      if (currentValue > maxValue) {
        setMaxValue(newMaxValue);
      }

      const startY = (canvasHeight / 2) - ((previousValue - minValue) / (maxValue - minValue)) * canvasHeight;
      const targetY = (canvasHeight / 2) - ((currentValue - minValue) / (maxValue - minValue)) * canvasHeight;
      const rectWidth = 30;
      const interval = setInterval(() => {
        progress += step;

        ctx.clearRect(0, 0, canvas.width / scale, canvas.height / scale);

        const updatedRectangles = rectangles.map((rect) => ({
          ...rect,
          x: rect.x - rectWidth,
        }));

        updatedRectangles.forEach((rect) => {
          ctx.fillStyle = rect.color;
          ctx.fillRect(rect.x, rect.y, rect.width, rect.height);
        });

        const height = Math.abs(progress);
        const rectY = difference > 0 ? startY - height : startY;
        ctx.fillStyle = difference > 0 ? 'green' : 'red';
        ctx.fillRect(canvas.width / scale / 2 - 15, rectY, rectWidth, height);

        if (Math.abs(progress) >= Math.abs(targetY - startY)) {
          clearInterval(interval);

          updatedRectangles.push({
            x: canvas.width / scale / 2 - 15,
            y: rectY,
            width: rectWidth,
            height: height,
            color: difference > 0 ? 'green' : 'red',
          });

          setRectangles(updatedRectangles);
          setPreviousValue(currentValue);
        }
      }, 20);

      return () => clearInterval(interval);
    }
  }, [currentValue, previousValue, rectangles, canvasHeight, minValue, maxValue]);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <canvas
        ref={canvasRef}
        style={{ width: 500, height: canvasHeight, border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      <button
        onClick={generateValue}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px'
        }}>
        Generate Value
      </button>
    </div>
  );
};

export default Page4;
