import React, { useState } from 'react';

const PercentageSlider = () => {
  const [value, setValue] = useState(0);

  const handleChange = (e) => {
    setValue(e.target.value);
  };

  return (
    <div style={{ width: '80%', margin: '0 auto', textAlign: 'center', position: 'relative' }}>
      <div style={{ fontSize: '26px', marginBottom: '20px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        {value}%
        <img 
          src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" 
          alt="Coin"
          style={{
            width: '30px',
            height: '30px',
            marginLeft: '10px',
          }}
        />
      </div>

      <div style={{ position: 'relative', width: '100%' }}>
        {/* Кружочки-метки на заднем фоне */}
        <div style={{ position: 'absolute', top: '50%', width: '100%', height: '8px', transform: 'translateY(-50%)', display: 'flex', justifyContent: 'space-between' }}>
          {[0, 25, 50, 75, 100].map((percentage) => (
            <div 
              key={percentage} 
              style={{ 
                width: '12px', 
                height: '12px', 
                borderRadius: '50%', 
                background: '#4CAF50', 
                transform: 'translateX(-50%)',
              }} 
            />
          ))}
        </div>

        <input 
          type="range" 
          min="0" 
          max="100" 
          value={value} 
          onChange={handleChange} 
          style={{ 
            width: '100%', 
            appearance: 'none', 
            height: '8px', 
            borderRadius: '4px', 
            background: 'transparent', 
            outline: 'none',
            cursor: 'pointer',
            zIndex: 1, // Для размещения поверх кружочков
          }} 
        />
      </div>
    </div>
  );
};

export default PercentageSlider;
