import React, { useState, useEffect } from 'react';

const Page4 = () => {
    const [previousValue, setPreviousValue] = useState(56000);
    const [currentValue, setCurrentValue] = useState(null);
    const [rectangles, setRectangles] = useState([]);
    const [isAnimating, setIsAnimating] = useState(false);

    const generateValue = () => {
        if (isAnimating) return;
        const newValue = Math.floor(Math.random() * (56345 - 55600 + 1)) + 55600;
        setCurrentValue(newValue);
        setIsAnimating(true);
    };

    useEffect(() => {
        if (currentValue !== null) {
            const difference = currentValue - previousValue;
            const isPositive = difference > 0;
            const newRectangle = {
                id: Date.now(),
                value: previousValue,
                targetValue: currentValue,
                step: difference / Math.abs(difference),
                height: Math.abs(difference),
                color: isPositive ? 'green' : 'red',
                top: isPositive ? previousValue : currentValue
            };

            setRectangles(prev => [...prev, newRectangle]);
        }
    }, [currentValue]);

    useEffect(() => {
        if (rectangles.length === 0) return;

        const lastRectangle = rectangles[rectangles.length - 1];
        let progress = 0;

        const interval = setInterval(() => {
            progress += 1;
            lastRectangle.top += lastRectangle.step;
            setRectangles([...rectangles]);

            if (progress >= lastRectangle.height) {
                clearInterval(interval);
                setPreviousValue(currentValue);
                setIsAnimating(false);
            }
        }, 10);

        return () => clearInterval(interval);
    }, [rectangles]);

    return (
        <div style={{ position: 'relative', height: '50vh', overflow: 'hidden' }}>
            {rectangles.map((rect, index) => (
                <div
                    key={rect.id}
                    style={{
                        position: 'absolute',
                        bottom: `${rect.top - 55600}px`,
                        left: `${index * 32}px`,
                        width: '30px',
                        height: `${rect.height}px`,
                        backgroundColor: rect.color,
                        transition: 'bottom 10ms linear',
                    }}
                />
            ))}
            <button onClick={generateValue} disabled={isAnimating} style={{ marginTop: '20px' }}>
                Generate Value
            </button>
        </div>
    );
};

export default Page4;
