import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
    const canvasRef = useRef(null);
    const [isRunning, setIsRunning] = useState(false);
    const [candles, setCandles] = useState([]);
    const [currentCandle, setCurrentCandle] = useState(null);

    const canvasWidth = 600;
    const canvasHeight = 400;
    const candleWidth = 20;
    const gap = 2;
    const centerYDefault = canvasHeight / 2;
    const centerX = canvasWidth / 2;

    let centerY = centerYDefault;

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvasWidth, canvasHeight);

        // Рисуем уже существующие свечи
        candles.forEach(candle => {
            drawCandle(ctx, candle);
        });

        // Рисуем текущую свечу в реальном времени
        if (currentCandle) {
            drawCandle(ctx, currentCandle);
        }
    }, [candles, currentCandle]);

    const drawCandle = (ctx, candle) => {
        const { open, close, high, low, xOffset, color } = candle;

        const bodyTop = centerY - Math.max(open, close);
        const bodyBottom = centerY - Math.min(open, close);
        const bodyHeight = Math.abs(open - close);

        // Фитиль верхний
        ctx.beginPath();
        ctx.moveTo(centerX + xOffset + candleWidth / 2, centerY - high);
        ctx.lineTo(centerX + xOffset + candleWidth / 2, bodyTop);
        ctx.strokeStyle = color;
        ctx.stroke();

        // Тело свечи
        ctx.beginPath();
        ctx.rect(centerX + xOffset, bodyTop, candleWidth, bodyHeight);
        ctx.fillStyle = color;
        ctx.fill();
        ctx.closePath();

        // Фитиль нижний
        ctx.beginPath();
        ctx.moveTo(centerX + xOffset + candleWidth / 2, bodyBottom);
        ctx.lineTo(centerX + xOffset + candleWidth / 2, centerY - low);
        ctx.strokeStyle = color;
        ctx.stroke();
    };

    const generateCandle = () => {
        const open = Math.floor(Math.random() * 200) - 100;
        const close = open + Math.floor(Math.random() * 41) - 20;
        const high = Math.max(open, close) + Math.floor(Math.random() * 20);
        const low = Math.min(open, close) - Math.floor(Math.random() * 20);
        const color = close > open ? 'green' : 'red';

        const lastCandle = candles[candles.length - 1];
        const previousXOffset = lastCandle ? lastCandle.xOffset - (candleWidth + gap) : 0;

        setCurrentCandle({ open, close, high, low, xOffset: previousXOffset, color });

        setCandles(prevCandles => {
            const updatedCandles = prevCandles.map(candle => ({
                ...candle,
                xOffset: candle.xOffset - (candleWidth + gap)
            }));

            return [...updatedCandles, { open, close, high, low, xOffset: previousXOffset, color }];
        });
    };

    useEffect(() => {
        if (!isRunning) return;

        const interval = setInterval(generateCandle, 1000);
        generateCandle();

        return () => clearInterval(interval);
    }, [isRunning]);

    const handleStartStop = () => {
        setIsRunning(prevState => !prevState);
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div>
                <canvas ref={canvasRef} width={canvasWidth} height={canvasHeight} style={{ border: '1px solid black' }} />
                <button onClick={handleStartStop} style={{ display: 'block', margin: '10px auto' }}>
                    {isRunning ? 'Stop' : 'Start'}
                </button>
            </div>
        </div>
    );
};

export default Page4;
