import React, { useState, useEffect } from 'react';

const Page4 = () => {
  const [rectangles, setRectangles] = useState([]);
  const [currentValue, setCurrentValue] = useState(0);
  const [animationState, setAnimationState] = useState({ height: 0, direction: 1 });

  useEffect(() => {
    // Function to generate a random value between -50 and 50
    const generateRandomValue = () => Math.floor(Math.random() * 101) - 50;

    // Function to animate the rectangle over 5 seconds
    const animateRectangle = (targetValue) => {
      const step = targetValue / 50; // 5 seconds, 100ms per step
      let currentStep = 0;

      const interval = setInterval(() => {
        setAnimationState((prev) => ({
          height: prev.height + step,
          direction: targetValue >= 0 ? 1 : -1,
        }));

        currentStep += 1;
        if (currentStep >= 50) {
          clearInterval(interval);
          // Store the completed rectangle and prepare for the next one
          setRectangles((prev) => [
            ...prev,
            { value: targetValue, start: currentValue },
          ]);
          setCurrentValue(targetValue);
          setAnimationState({ height: 0, direction: targetValue >= 0 ? 1 : -1 });
        }
      }, 100);
    };

    // Start the initial animation
    animateRectangle(generateRandomValue());
  }, [currentValue]);

  return (
    <div style={{ width: '100%', height: '100vh', position: 'relative', overflow: 'hidden' }}>
      {rectangles.map((rect, index) => (
        <div
          key={index}
          style={{
            position: 'absolute',
            left: `calc(50% - ${rectangles.length - index} * 40px)`, // Shift left for each previous rectangle
            top: '25%',
            width: '30px',
            height: `${Math.abs(rect.value)}px`,
            backgroundColor: rect.value > 0 ? 'green' : 'red',
            transform: `translateY(${rect.value < 0 ? 0 : -Math.abs(rect.value)}px)`,
          }}
        />
      ))}
      <div
        style={{
          position: 'absolute',
          left: '50%',
          top: '25%',
          width: '30px',
          height: `${Math.abs(animationState.height)}px`,
          backgroundColor: animationState.direction > 0 ? 'green' : 'red',
          transform: `translateY(${animationState.direction < 0 ? 0 : -Math.abs(animationState.height)}px)`,
        }}
      />
    </div>
  );
};

export default Page4;
