import React, { useState, useEffect } from 'react';

const Page4 = () => {
  const [balance, setBalance] = useState(0);

  useEffect(() => {
    const getInitialData = () => {
      window.Telegram.WebApp.CloudStorage.getItems(['balance'], (error, result) => {
        if (error) {
          console.error('Failed to get initial data from cloud storage:', error);
        } else {
          const initialBalance = result.balance ? parseInt(result.balance, 10) : 0;
          setBalance(initialBalance);
        }
      });
    };

    getInitialData();
  }, []);

  return (
    <div>
      <div
        style={{
          position: 'absolute',
          top: '15px',
          left: '10px',
          display: 'flex',
          alignItems: 'center',
          fontSize: '1.2em',
          opacity: 0,
          transform: 'translateX(100px)',
          transition: 'transform 0.4s ease-out, opacity 0.4s ease-out',
          animation: 'slideIn 0.4s forwards',
        }}
      >
        <img
          src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png"
          alt="Balance Icon"
          style={{
            width: '20px',
            height: '20px',
            marginRight: '5px',
          }}
        />
        {balance.toLocaleString()}
      </div>

      <div style={{ marginTop: '100px', padding: '20px', textAlign: 'center' }}>
        <iframe
          src="https://widget.tradingview.com/?symbol=BTCUSDT&interval=30"
          width="100%"
          height="300px"
          frameBorder="0"
          scrolling="no"
          style={{
            border: '1px solid #ccc',
            borderRadius: '4px',
          }}
        ></iframe>
      </div>
    </div>
  );
};

export default Page4;
