import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const [values, setValues] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [currentValue, setCurrentValue] = useState(0);
  const [dataPoints, setDataPoints] = useState([]);
  const [centerValue, setCenterValue] = useState(0); // Центр шкалы
  const [range, setRange] = useState(1000); // Полный диапазон шкалы (±200)
  const canvasRef = useRef(null);

  // Функция для получения данных с CoinGecko
  const fetchBitcoinPrices = async () => {
    try {
      const response = await fetch('https://api.coingecko.com/api/v3/coins/bitcoin/market_chart?vs_currency=usd&days=1&interval=minute');
      const data = await response.json();
      const prices = data.prices.map(price => price[1]); // Берём только цены
      setValues(prices);
      setCurrentValue(prices[0]);
      setCenterValue(prices[0]);
    } catch (error) {
      console.error('Ошибка при получении данных:', error);
    }
  };

  useEffect(() => {
    fetchBitcoinPrices();
  }, []);

  useEffect(() => {
    if (currentIndex < values.length) {
      const targetValue = values[currentIndex];
      const difference = Math.abs(targetValue - currentValue);

      const intervalDuration = 1500 / difference;

      const interval = setInterval(() => {
        setCurrentValue(prevValue => {
          if (prevValue < targetValue) {
            return prevValue + 1;
          } else if (prevValue > targetValue) {
            return prevValue - 1;
          } else {
            setDataPoints(prev => [...prev, prevValue]);
            setCurrentIndex(currentIndex + 1);
            return prevValue;
          }
        });
      }, intervalDuration);

      return () => clearInterval(interval);
    }
  }, [currentIndex, currentValue, values]);

  useEffect(() => {
    if (currentValue < centerValue - range / 2 || currentValue > centerValue + range / 2) {
      setCenterValue(currentValue);
    }
  }, [currentValue, centerValue, range]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const scale = window.devicePixelRatio || 1;
    canvas.width = 500 * scale;
    canvas.height = 300 * scale;
    ctx.scale(scale, scale);

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (dataPoints.length > 1) {
      ctx.strokeStyle = '#FFD700';
      ctx.lineWidth = 1.5;
      ctx.beginPath();

      dataPoints.forEach((value, index) => {
        const x = (canvas.width / scale / 2) + index * 10 - dataPoints.length * 10;
        const y = canvas.height / scale - ((value - (centerValue - range / 2)) / range) * canvas.height / scale;
        if (index === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }
      });

      ctx.stroke();

      const fixedY = canvas.height / scale - ((currentValue - (centerValue - range / 2)) / range) * canvas.height / scale;
      ctx.strokeStyle = 'lightblue';
      ctx.lineWidth = 1;
      ctx.beginPath();
      ctx.moveTo(0, fixedY);
      ctx.lineTo(canvas.width / scale, fixedY);
      ctx.stroke();

      ctx.font = '12px Arial';
      ctx.fillStyle = 'lightblue';
      ctx.fillText(`$${currentValue.toFixed(2)}`, canvas.width / scale - 150, fixedY - 10);
    }
  }, [dataPoints, centerValue, range, currentValue]);

  const handleFixValue = () => {
    setCenterValue(currentValue);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <canvas
        ref={canvasRef}
        style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      <button
        onClick={handleFixValue}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px'
        }}>
        Зафиксировать текущее значение
      </button>
    </div>
  );
};

export default Page4;
