import React, { useState, useEffect } from "react";
import styled, { keyframes } from "styled-components";

// Анимация для плавного перемещения
const moveAnimation = (x, y) => keyframes`
  0% {
    transform: translate(${x}px, ${y}px);
  }
  100% {
    transform: translate(${x}px, ${y}px);
  }
`;

// Стиль для шариков-кнопок
const Ball = styled.button`
  position: absolute;
  width: ${(props) => props.size}px;
  height: ${(props) => props.size}px;
  border-radius: 50%;
  background-color: #3498db;
  animation: ${(props) => moveAnimation(props.x, props.y)} 1s ease;
  cursor: pointer;
  border: none;
`;

// Функция для создания начальных позиций и размеров шаров
const createBalls = () => {
  const balls = [];
  for (let i = 0; i < 9; i++) {
    balls.push({
      id: i,
      size: Math.random() * 50 + 30, // Размер шара от 30 до 80 пикселей
      x: Math.random() * 400, // Случайные начальные позиции
      y: Math.random() * 400,
    });
  }
  return balls;
};

const LevelPage = () => {
  const [balls, setBalls] = useState(createBalls);

  // Функция для удаления шара
  const handleRemoveBall = (id) => {
    setBalls((prevBalls) => prevBalls.filter((ball) => ball.id !== id));
    repositionBalls();
  };

  // Функция для пересчета позиций шаров
  const repositionBalls = () => {
    setBalls((prevBalls) =>
      prevBalls.map((ball) => {
        // Простое перемещение шаров вниз после удаления одного
        return {
          ...ball,
          x: Math.random() * 400, // Простая логика для случайного смещения
          y: Math.random() * 400,
        };
      })
    );
  };

  return (
    <div>
      {balls.map((ball) => (
        <Ball
          key={ball.id}
          size={ball.size}
          x={ball.x}
          y={ball.y}
          onClick={() => handleRemoveBall(ball.id)}
        />
      ))}
    </div>
  );
};

export default LevelPage;
