import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';

const Page4 = () => {
  const [balance, setBalance] = useState(0);
  const [bet, setBet] = useState('');
  const [btcPrice, setBtcPrice] = useState(null);
  const [entryPrice, setEntryPrice] = useState(null);
  const [message, setMessage] = useState('Select your bet');
  const [timer, setTimer] = useState(30);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isWin, setIsWin] = useState(null);
  const ws = useRef(null);

  // Подключение к WebSocket для получения цены биткоина в реальном времени
  useEffect(() => {
    ws.current = io('wss://ws-feed.pro.coinbase.com');

    const subscribe = {
      type: 'subscribe',
      channels: [{ name: 'ticker', product_ids: ['BTC-USD'] }]
    };

    ws.current.on('connect', () => {
      ws.current.send(JSON.stringify(subscribe));
    });

    ws.current.on('message', (data) => {
      const response = JSON.parse(data);
      if (response.type === 'ticker' && response.product_id === 'BTC-USD') {
        setBtcPrice(parseFloat(response.price));
      }
    });

    ws.current.on('disconnect', () => {
      console.log('Disconnected from WebSocket');
    });

    return () => {
      ws.current.close();
    };
  }, []);

  useEffect(() => {
    const getInitialData = () => {
      window.Telegram.WebApp.CloudStorage.getItems(['balance'], (error, result) => {
        if (error) {
          console.error('Failed to get initial data from cloud storage:', error);
        } else {
          const initialBalance = result.balance ? parseInt(result.balance, 10) : 0;
          setBalance(initialBalance);
        }
      });
    };

    getInitialData();

  }, []);

  useEffect(() => {
    if (timer > 0 && isPlaying) {
      const countdown = setTimeout(() => {
        setTimer(timer - 1);
      }, 1000);
      return () => clearTimeout(countdown);
    } else if (timer === 0 && isPlaying) {
      finalizeBet();
    }
  }, [timer, isPlaying]);

  const handleBet = (percentage) => {
    const betValue = Math.floor(balance * percentage);
    setBet(betValue);
  };

  const handleBuySell = (action) => {
    if (btcPrice && bet) {
      setIsPlaying(true);
      setEntryPrice(btcPrice);
      setBalance(balance - bet);
      setTimer(30);
      setMessage(action === 'buy' ? 'Buying...' : 'Selling...');
      setIsWin(null);  // Сброс состояния выигрыша перед началом нового раунда
    }
  };

  const finalizeBet = () => {
    setIsPlaying(false);
    let result = false;
    if (entryPrice) {
      if (message === 'Buying...' && btcPrice > entryPrice) {
        setBalance(balance + bet * 1.98);  // Увеличенная ставка
        result = true;
      } else if (message === 'Selling...' && btcPrice < entryPrice) {
        setBalance(balance + bet * 1.98);  // Увеличенная ставка
        result = true;
      }
    }
    setIsWin(result);
    setMessage(result ? 'You Win!' : 'You Lose');
    setEntryPrice(null);
    setBet('');
    setTimer(30);  // Сброс таймера после завершения раунда
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100vh',
      backgroundColor: '#1a181c',
      color: 'white',
      overflow: 'hidden',
      position: 'relative'
    }}>
      <div style={{
        position: 'absolute',
        top: '10px',
        left: '10px',
        fontSize: '1.2em',
        padding: '5px 10px',
        borderRadius: '5px'
      }}>
        🎫 Tickets
      </div>
      <div style={{
        position: 'absolute',
        top: '10px',
        right: '10px',
        display: 'flex',
        alignItems: 'center',
        fontSize: '1.2em'
      }}>
        <img src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" alt="Balance" style={{ width: '20px', marginRight: '5px' }} />
        {balance.toLocaleString()}
      </div>

      <div style={{
        marginBottom: '20px',
        fontSize: '2em',
        fontWeight: 'bold',
        textAlign: 'center',
        color: isWin === null ? 'white' : isWin ? 'green' : 'red'
      }}>
        {entryPrice ? `Entry Price: $${entryPrice}` : 'No active trade'}
      </div>

      <div style={{
        marginBottom: '20px',
        fontSize: '2em',
        fontWeight: 'bold',
        textAlign: 'center'
      }}>
        Current Price: {btcPrice ? `$${btcPrice.toFixed(2)}` : 'Loading...'}
      </div>

      <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'center', gap: '10px', marginBottom: '20px' }}>
        <button onClick={() => handleBet(0.1)} style={betButtonStyle}>10%</button>
        <button onClick={() => handleBet(0.25)} style={betButtonStyle}>25%</button>
        <button onClick={() => handleBet(0.5)} style={betButtonStyle}>50%</button>
        <button onClick={() => handleBet(1)} style={betButtonStyle}>100%</button>
      </div>

      <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'center', gap: '10px', marginBottom: '20px' }}>
        <button onClick={() => handleBuySell('buy')} disabled={isPlaying || !bet} style={actionButtonStyle}>Buy</button>
        <button onClick={() => handleBuySell('sell')} disabled={isPlaying || !bet} style={actionButtonStyle}>Sell</button>
      </div>

      {isPlaying && (
        <div style={{ fontSize: '1.5em', marginBottom: '20px' }}>
          Time Left: {timer} seconds
        </div>
      )}

      <div style={{ fontSize: '1.5em', color: message === 'You Win!' ? 'green' : message === 'You Lose' ? 'red' : 'white' }}>
        {message}
      </div>
    </div>
  );
};

const betButtonStyle = {
  fontSize: '12px',
  backgroundColor: '#333333',
  color: 'white',
  borderRadius: '12px',
  padding: '10px 20px',
  cursor: 'pointer',
  width: '80px',
  textAlign: 'center'
};

const actionButtonStyle = {
  fontSize: '16px',
  backgroundColor: '#007bff',
  color: 'white',
  borderRadius: '12px',
  padding: '10px 20px',
  cursor: 'pointer',
  width: '120px',
  textAlign: 'center'
};

export default Page4;
