import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
    const [candles, setCandles] = useState([]);
    const [currentValue, setCurrentValue] = useState(100);
    const canvasRef = useRef(null);

    useEffect(() => {
        const interval = setInterval(() => {
            const newValue = currentValue + (Math.random() * 20 - 10); // Генерация случайного значения
            setCandles((prevCandles) => [...prevCandles, { open: currentValue, close: newValue }]);
            setCurrentValue(newValue);
        }, 3000); // Каждые 3 секунды генерируем новую свечу

        return () => clearInterval(interval);
    }, [currentValue]);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvas.width, canvas.height); // Очищаем канвас перед каждым перерисовыванием

        let x = canvas.width - 40; // Начальная позиция для рисования свечей (с правого края)
        candles.slice(-10).forEach((candle) => { // Отображаем последние 10 свечей
            const color = candle.close >= candle.open ? 'green' : 'red';
            const height = Math.abs(candle.close - candle.open);
            const y = canvas.height / 2 - (candle.close >= candle.open ? candle.close : candle.open);

            ctx.fillStyle = color;
            ctx.fillRect(x, y, 30, height);
            x -= 40; // Отступ для следующей свечи
        });
    }, [candles]);

    return (
        <div>
            <canvas ref={canvasRef} width={400} height={200} style={{ border: '1px solid #000' }} />
        </div>
    );
};

export default Page4;
