import React, { useState } from 'react';
import { useSprings, animated } from 'react-spring';
import './LevelPage.css';

const bubbleLayout = (bubbles) => {
  const center = { x: 0, y: 0 };
  const radius = 100; // Distance from the center for outer bubbles
  const angleStep = (Math.PI * 2) / (bubbles.length - 1);

  return bubbles.map((bubble, index) => {
    if (index === 0) return center; // Central bubble

    const angle = index * angleStep;
    return {
      x: radius * Math.cos(angle),
      y: radius * Math.sin(angle),
    };
  });
};

const LevelPage = () => {
  const initialBubbles = [0, 1, 2, 3, 4, 5, 6, 7];
  const [bubbles, setBubbles] = useState(initialBubbles);

  // Update the bubble positions using react-spring's useSprings
  const [springs, api] = useSprings(bubbles.length, (index) => ({
    from: { x: 0, y: 0, opacity: 1 },
    to: bubbleLayout(bubbles)[index],
    config: { tension: 300, friction: 20 },
  }));

  // Remove bubble and update the positions
  const handleBubbleClick = (index) => {
    const updatedBubbles = bubbles.filter((_, i) => i !== index);
    setBubbles(updatedBubbles);

    api.start((i) => {
      const newLayout = bubbleLayout(updatedBubbles);
      return {
        to: { x: newLayout[i].x, y: newLayout[i].y, opacity: 1 },
      };
    });
  };

  return (
    <div className="level-home">
      <div className="level-header">
        <h1>Level Page</h1>
        <div className="bubble-container">
          {springs.map((springProps, index) => (
            <animated.div
              key={index}
              className="bubble"
              style={{
                transform: springProps.x.to((x) => `translateX(${x}px)`).concat(
                  springProps.y.to((y) => `translateY(${y}px)`)
                ),
                opacity: springProps.opacity,
              }}
              onClick={() => handleBubbleClick(index)}
            >
              {index + 1}
            </animated.div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LevelPage;
