import React, { useState } from 'react';
import { useSprings, animated } from 'react-spring';
import './LevelPage.css';

const initialButtons = Array.from({ length: 10 }, (_, i) => ({
  id: i,
  text: `Button ${i + 1}`,
  imageUrl: `https://via.placeholder.com/150?text=Img+${i + 1}`
}));

const LevelPage = () => {
  const [buttons, setButtons] = useState(initialButtons);

  const [springs, api] = useSprings(
    buttons.length,
    index => ({
      from: { transform: `translate(${Math.random() * 500}px, ${Math.random() * 400}px)` },
    })
  );

  const handleButtonClick = (id) => {
    setButtons(buttons.filter(button => button.id !== id));
  };

  return (
    <div className="level-home">
      <header className="level-header">
        <h1>Level Page</h1>
      </header>
      <div className="buttons-container">
        {springs.map((props, index) => (
          <animated.button
            key={buttons[index].id}
            style={props}
            className="capsule-button"
            onClick={() => handleButtonClick(buttons[index].id)}
          >
            <div
              className="button-bg"
              style={{ backgroundImage: `url(${buttons[index].imageUrl})` }}
            />
            <span>{buttons[index].text}</span>
          </animated.button>
        ))}
      </div>
    </div>
  );
};

export default LevelPage;
