import React, { useState } from 'react';
import { Slider } from 'rsuite';
import 'rsuite/dist/rsuite.min.css';

const PercentageSlider = () => {
  const [value, setValue] = useState(0);

  return (
    <div style={{ width: '80%', margin: '0 auto', textAlign: 'center' }}>
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginBottom: '20px' }}>
        <h4 style={{ marginRight: '10px' }}>{value}%</h4>
        <img src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" alt="Coins" style={{ height: '30px' }} />
      </div>
      <Slider
        value={value}
        onChange={value => setValue(value)}
        min={0}
        max={100}
        step={5}
        graduated
        progress
        defaultValue={0}
        barClassName="custom-bar"
        handleClassName="custom-handle"
        handleStyle={{
          borderColor: '#FFD700',
          backgroundColor: '#FFD700',
          borderWidth: '2px',
          width: '14px',
          height: '14px',
        }}
      />
      <style jsx>{`
        .custom-bar {
          background-color: #2F2F2F;
        }

        .rs-slider-bar-progress {
          background-color: #FFA500;
        }

        .custom-handle:hover {
          border-color: #FFA500;
        }
      `}</style>
    </div>
  );
};

export default PercentageSlider;
