import React, { useState, useEffect } from 'react';
import './Bubbles.css';

const Bubble = ({ id, size, position, onClick }) => {
  return (
    <div
      className="bubble"
      style={{
        width: `${size}px`,
        height: `${size}px`,
        left: `${position.x}px`,
        top: `${position.y}px`,
        transform: `translate(-50%, -50%)`,
      }}
      onClick={() => onClick(id)}
    />
  );
};

const Bubbles = () => {
  const [bubbles, setBubbles] = useState([
    { id: 1, size: 80, position: { x: 100, y: 100 } },
    { id: 2, size: 60, position: { x: 200, y: 150 } },
    { id: 3, size: 90, position: { x: 300, y: 100 } },
    { id: 4, size: 50, position: { x: 150, y: 250 } },
    { id: 5, size: 70, position: { x: 250, y: 250 } },
    { id: 6, size: 60, position: { x: 350, y: 200 } },
    { id: 7, size: 40, position: { x: 180, y: 300 } },
    { id: 8, size: 50, position: { x: 300, y: 300 } },
    { id: 9, size: 60, position: { x: 400, y: 250 } },
  ]);

  const [center] = useState({ x: window.innerWidth / 2, y: window.innerHeight / 2 });

  const removeBubble = (id) => {
    setBubbles((prevBubbles) => prevBubbles.filter((bubble) => bubble.id !== id));
  };

  useEffect(() => {
    const interval = setInterval(() => {
      setBubbles((prevBubbles) =>
        prevBubbles.map((bubble) => {
          const distanceX = center.x - bubble.position.x;
          const distanceY = center.y - bubble.position.y;
          const distance = Math.sqrt(distanceX ** 2 + distanceY ** 2);

          if (distance > 5) {
            const moveX = distanceX / distance;
            const moveY = distanceY / distance;
            return {
              ...bubble,
              position: {
                x: bubble.position.x + moveX * 2, // скорость движения к центру
                y: bubble.position.y + moveY * 2,
              },
            };
          }
          return bubble;
        })
      );
    }, 100);

    return () => clearInterval(interval);
  }, [center]);

  return (
    <div className="bubbles-container">
      {bubbles.map((bubble) => (
        <Bubble
          key={bubble.id}
          id={bubble.id}
          size={bubble.size}
          position={bubble.position}
          onClick={removeBubble}
        />
      ))}
    </div>
  );
};

export default Bubbles;
