import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const [centerValue, setCenterValue] = useState(56000); // центр графика
  const [previousValue, setPreviousValue] = useState(56000);
  const [currentValue, setCurrentValue] = useState(56000);
  const [rectangles, setRectangles] = useState([]); // массив для хранения прямоугольников
  const canvasRef = useRef(null);

  const fetchBitcoinPrice = async () => {
    try {
      const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice/BTC.json');
      const data = await response.json();
      const bitcoinPrice = Math.floor(data.bpi.USD.rate_float); // текущая цена биткоина
      const adjustment = Math.random() > 0.5 ? 120 : -120; // случайное изменение
      setCurrentValue(bitcoinPrice + adjustment);

      // Установить начальное значение центра
      if (centerValue === 56000) {
        setCenterValue(bitcoinPrice);
        setPreviousValue(bitcoinPrice);
        setCurrentValue(bitcoinPrice + adjustment);
      }
    } catch (error) {
      console.error('Error fetching Bitcoin price:', error);
    }
  };

  useEffect(() => {
    fetchBitcoinPrice(); // Запрос цены биткоина при загрузке страницы
    const intervalId = setInterval(fetchBitcoinPrice, 60000); // Обновление цены каждую минуту
    return () => clearInterval(intervalId); // Очистка интервала при размонтировании компонента
  }, []);

  useEffect(() => {
    if (currentValue !== previousValue) {
      if (currentValue < centerValue - 500 || currentValue > centerValue + 500) {
        // Если значение выходит за пределы диапазона, обновляем центр
        setCenterValue(currentValue);
      }

      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');

      const scale = window.devicePixelRatio || 1;
      canvas.width = 500 * scale;
      canvas.height = 300 * scale;
      ctx.scale(scale, scale);

      const difference = currentValue - previousValue;
      const step = difference > 0 ? 1 : -1;
      let progress = 0;

      const startY = 150 - ((previousValue - centerValue) / 500) * 120;
      const targetY = 150 - ((currentValue - centerValue) / 500) * 120;
      const rectWidth = 30;
      const interval = setInterval(() => {
        progress += step;

        ctx.clearRect(0, 0, canvas.width / scale, canvas.height / scale);

        // Смещение всех прямоугольников влево
        const updatedRectangles = rectangles.map((rect) => ({
          ...rect,
          x: rect.x - rectWidth,
        }));

        // Рисование всех прямоугольников
        updatedRectangles.forEach((rect) => {
          ctx.fillStyle = rect.color;
          ctx.fillRect(rect.x, rect.y, rect.width, rect.height);
        });

        // Рисование текущего прямоугольника
        const height = Math.abs(progress);
        const rectY = difference > 0 ? startY - height : startY;
        ctx.fillStyle = difference > 0 ? 'green' : 'red';
        ctx.fillRect(canvas.width / scale / 2 - 15, rectY, rectWidth, height);

        if (Math.abs(progress) >= Math.abs(targetY - startY)) {
          clearInterval(interval);

          // Добавляем новый прямоугольник в массив
          updatedRectangles.push({
            x: canvas.width / scale / 2 - 15,
            y: rectY,
            width: rectWidth,
            height: height,
            color: difference > 0 ? 'green' : 'red',
          });

          setRectangles(updatedRectangles);
          setPreviousValue(currentValue);
        }
      }, 20);

      return () => clearInterval(interval);
    }
  }, [currentValue, previousValue, rectangles, centerValue]);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <canvas
        ref={canvasRef}
        style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
    </div>
  );
};

export default Page4;
