import React, { useState } from 'react';

const PercentageSlider = () => {
  const [value, setValue] = useState(0);

  const handleChange = (e) => {
    setValue(Number(e.target.value));
  };

  const handleClick = (newValue) => {
    setValue(newValue);
  };

  const marks = [0, 25, 50, 75, 100];

  return (
    <div style={{ width: '80%', margin: '0 auto', textAlign: 'center' }}>
      <div style={{ fontSize: '26px', marginBottom: '20px', position: 'relative' }}>
        {value}%
        <img 
          src="https://kairosrainbow.it/wp-content/uploads/2016/11/coins.png" 
          alt="Coin"
          style={{
            width: '30px',
            height: '30px',
            marginLeft: '10px',
            position: 'absolute',
            top: '50%',
            transform: 'translateY(-50%)',
          }}
        />
      </div>

      <div style={{ position: 'relative', width: '100%', height: '50px', display: 'flex', alignItems: 'center' }}>
        <div style={{ width: '100%', height: '4px', backgroundColor: '#ccc', position: 'absolute', top: '50%', transform: 'translateY(-50%)' }}></div>

        {marks.map((mark, index) => (
          <div
            key={mark}
            onClick={() => handleClick(mark)}
            style={{
              position: 'absolute',
              left: `${(mark / 100) * 100}%`,
              transform: 'translateX(-50%)',
              zIndex: 1,
              width: '20px',
              height: '20px',
              borderRadius: '50%',
              backgroundColor: mark <= value ? '#007bff' : '#ccc',
              cursor: 'pointer',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <div
              style={{
                position: 'absolute',
                bottom: '-20px',
                fontSize: '14px',
                whiteSpace: 'nowrap',
              }}
            >
              {mark}%
            </div>
          </div>
        ))}

        <input 
          type="range" 
          min="0" 
          max="100" 
          value={value} 
          onChange={handleChange} 
          style={{
            position: 'relative',
            zIndex: 2,
            width: '100%',
            appearance: 'none',
            height: '20px',
            background: 'transparent',
            pointerEvents: 'none',
          }}
        />
        <div
          style={{
            position: 'absolute',
            left: `${(value / 100) * 100}%`,
            transform: 'translate(-50%, -50%)',
            width: '28px',
            height: '28px',
            borderRadius: '50%',
            backgroundColor: '#007bff',
            zIndex: 3,
            pointerEvents: 'auto',
            cursor: 'pointer',
          }}
        ></div>
      </div>
    </div>
  );
};

export default PercentageSlider;
