import React, { useEffect, useState } from 'react';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [level, setLevel] = useState(1);
  const [progress, setProgress] = useState(0); // Прогресс в процентах

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
  }, []);

  // Обработчик нажатия на кнопку для увеличения прогресса
  const handleIncreaseProgress = () => {
    if (progress >= 100) {
      alert('Level Up!');
      setLevel(prev => prev + 1);
      setProgress(0);
    } else {
      setProgress(prev => prev + 20);
    }
  };

  // Функция для смены цвета прогресса в зависимости от уровня
  const getProgressColor = () => {
    const colors = ['#4caf50', '#2196f3', '#ff9800', '#9c27b0', '#e91e63']; // Зеленый, Синий, Оранжевый, Фиолетовый, Розовый
    return colors[(level - 1) % colors.length];
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      height: '100vh',
      textAlign: 'center',
      color: 'inherit',
      paddingBottom: '80px',
      fontFamily: "'Chakra Petch', sans-serif"
    }}>
      {/* Верхняя часть */}
      <div style={{
        width: '100%',
        height: headerExpand ? 'calc(65vh + 120px)' : '120px',
        boxShadow: '0 1px 50px rgba(93, 95, 98, 0.6)',
        backgroundColor: 'rgba(168, 8%, 91%, 0.76)',
        borderBottomLeftRadius: '50% 20%',
        borderBottomRightRadius: '50% 20%',
        position: 'absolute',
        top: 0,
        left: 0,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
        transition: 'height 0.75s ease-in-out',
        flexDirection: 'column' // Размещаем элементы вертикально
      }}>
        <div style={{
          position: 'absolute',
          width: '200%',
          height: '200%',
          background: "url('https://www.transparenttextures.com/patterns/stardust.png') repeat",
          animation: 'level-moveBackground 20s linear infinite'
        }}></div>

        {/* Текущий уровень */}
        <h1 style={{
          fontFamily: "'Audiowide', sans-serif",
          color: '#fff',
          fontSize: '24px',
          zIndex: '2'
        }}>{level} LEVEL</h1>

        {/* Полоска прогресса */}
        <div style={{
          position: 'relative',
          width: '56%', // Уменьшили ширину на 30%
          height: '15px', // Уменьшили высоту в 2 раза
          margin: '20px auto', // Отступы сверху и снизу
          backgroundColor: 'rgba(221, 221, 221, 0.1)', // Сделали фон на 80% прозрачным
          borderRadius: '10px',
          overflow: 'hidden',
          zIndex: '2'
        }}>
          <div style={{
            height: '100%',
            width: `${progress}%`,
            backgroundColor: getProgressColor(), // Меняем цвет прогресса в зависимости от уровня
            transition: 'width 0.5s ease',
          }}>
            {/* Вертикальные полоски, делящие полоску прогресса на 5 частей */}
            {[...Array(5)].map((_, i) => (
              <div key={i} style={{
                position: 'absolute',
                height: '100%',
                width: '2px',
                backgroundColor: 'rgba(221, 221, 221, 0.)',
                left: `${(i + 1) * 20}%`,
                top: 0
              }} />
            ))}
          </div>
        </div>

        {/* Кнопка для увеличения прогресса */}
        <button onClick={handleIncreaseProgress} style={{
          marginTop: '10px',
          backgroundColor: getProgressColor(),
          color: '#fff',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          fontSize: '14px', // Уменьшили текст на кнопке
          zIndex: '2',
          padding: '10px 20px' // Увеличили ширину кнопки
        }}>
          Increase Progress
        </button>

        <div style={{marginTop: "50px", backgroundColor:'gray', borderRadius: '20px', height: '50px', }}></div>
      </div>

      {/* Добавление анимации через тег style */}
      <style>{`
        @keyframes level-moveBackground {
          0% { background-position: 0 0; }
          100% { background-position: -1000px -1000px; }
        }
      `}</style>
    </div>
  );
};

export default LevelPage;
