import React, { useState, useEffect } from 'react';

const Candle = ({ open, close, high, low }) => {
  const isGrowing = close >= open;
  const candleColor = isGrowing ? 'green' : 'red';

  return (
    <div style={{ position: 'relative', display: 'inline-block', marginRight: '10px' }}>
      {/* Линия */}
      <div
        style={{
          position: 'absolute',
          left: '50%',
          transform: 'translateX(-50%)',
          width: '2px',
          height: `${high - low}px`,
          bottom: `${low}px`,
          backgroundColor: 'black',
        }}
      />

      {/* Прямоугольник свечи */}
      <div
        style={{
          width: '30px',
          height: `${Math.abs(close - open)}px`,
          backgroundColor: candleColor,
          position: 'absolute',
          bottom: `${Math.min(open, close)}px`,
        }}
      />
    </div>
  );
};

const Page4 = () => {
  const [candles, setCandles] = useState([]);
  const [centerIndex, setCenterIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      const open = candles.length > 0 ? candles[candles.length - 1].close : 50;
      const close = open + Math.floor(Math.random() * 200 - 100);
      const high = Math.max(open, close) + Math.floor(Math.random() * 50);
      const low = Math.min(open, close) - Math.floor(Math.random() * 50);

      setCandles(prev => {
        const newCandles = [...prev, { open, close, high, low }];
        if (newCandles.length > 20) {
          newCandles.shift(); // Удаляем старую свечу, если их больше 20
        }
        return newCandles;
      });

      setCenterIndex(prev => prev + 1);
    }, 10000);

    return () => clearInterval(interval);
  }, [candles]);

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ position: 'relative', width: '600px', height: '300px', border: '1px solid black', overflow: 'hidden' }}>
        <div style={{ position: 'absolute', left: `${-centerIndex * 40 + 300}px`, display: 'flex', bottom: '0' }}>
          {candles.map((candle, index) => (
            <Candle key={index} open={candle.open} close={candle.close} high={candle.high} low={candle.low} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Page4;
