import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [elementsVisible, setElementsVisible] = useState(false);
  const [level, setLevel] = useState(1);
  const [progress, setProgress] = useState(0);
  const [balance, setBalance] = useState(0);
  const [balanceRate2, setBalanceRate2] = useState(1);
  const [isExiting, setIsExiting] = useState(false);
  const navigate = useNavigate();

  // Массивы для текста слева и справа
  const leftTexts = ['Left Text 1', 'Left Text 2', 'Left Text 3'];
  const rightTexts = ['Right Text 1', 'Right Text 2', 'Right Text 3'];

  // Логика для увеличения прогресса в зависимости от баланса и balanceRate2
  const handleIncreaseProgress = () => {
    const thresholds = {
      5: 1, 10: 2, 20: 3, 40: 4, 80: 5,
      160: 6, 320: 7, 640: 8, 1280: 9, 2560: 10,
      5120: 11, 10240: 12, 20480: 13, 40960: 14, 81920: 15
    };

    const currentRate = thresholds[balance] || balanceRate2;

    if (progress >= 100) {
      alert('Level Up!');
      setLevel(prev => prev + 1);
      setProgress(0);

      // Сохраняем новый левел и сброс прогресса в cloudStorage
      window.Telegram.WebApp.CloudStorage.setItem('level2', (level + 1).toString(), (error, success) => {
        if (error) {
          console.error('Error setting level:', error);
        }
      });
      window.Telegram.WebApp.CloudStorage.setItem('progress2', '0', (error, success) => {
        if (error) {
          console.error('Error setting progress:', error);
        }
      });
    } else if (balance >= Object.keys(thresholds)[balanceRate2 - 1]) {
      setProgress(prev => prev + 20);

      // Обновляем прогресс в cloudStorage
      window.Telegram.WebApp.CloudStorage.setItem('progress2', (progress + 20).toString(), (error, success) => {
        if (error) {
          console.error('Error setting progress:', error);
        }
      });

      // Если баланс позволяет повысить rate, обновляем его
      if (balance >= Object.keys(thresholds)[balanceRate2]) {
        setBalanceRate2(currentRate + 1);
        window.Telegram.WebApp.CloudStorage.setItem('balanceRate2', (currentRate + 1).toString(), (error, success) => {
          if (error) {
            console.error('Error setting balanceRate2:', error);
          }
        });
      }
    }
  };

  useEffect(() => {
    // Получаем данные из cloudStorage при загрузке страницы
    const getInitialData = () => {
      window.Telegram.WebApp.CloudStorage.getItems(['balance', 'balanceRate2', 'level2', 'progress2'], (error, result) => {
        if (error) {
          console.error('Failed to get initial data from cloud storage:', error);
        } else {
          const initialBalance = result.balance ? parseInt(result.balance, 10) : 0;
          const initialRate = result.balanceRate2 ? parseInt(result.balanceRate2, 10) : 1;
          const initialLevel = result.level2 ? parseInt(result.level2, 10) : 1;
          const initialProgress = result.progress2 ? parseInt(result.progress2, 10) : 0;

          setBalance(initialBalance);
          setBalanceRate2(initialRate);
          setLevel(initialLevel);
          setProgress(initialProgress);
        }
      });
    };

    getInitialData();
  }, []);
  useEffect(() => {
    const { Telegram } = window;
    if (Telegram.WebApp) {
      Telegram.WebApp.BackButton.show();
      Telegram.WebApp.BackButton.onClick(() => {
        setIsExiting(true);

        // Задержка перед переходом для завершения анимации (0.75s)
        setTimeout(() => {
          navigate('/'); // Переход на главную страницу после завершения анимации
        }, 850); // Время на обратную анимацию
      });
    }

    return () => {
      if (Telegram.WebApp) {
        Telegram.WebApp.BackButton.offClick();
        Telegram.WebApp.BackButton.hide();
      }
    };
  }, [navigate]);

  useEffect(() => {
    // Сначала запускаем анимацию опускания header
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);

    // После завершения анимации высоты (через 0.75s) показываем остальные элементы
    setTimeout(() => {
      setElementsVisible(true);
    }, 550); // Длительность transition на высоту + небольшая задержка
  }, []);



  // Функция для смены цвета прогресса в зависимости от уровня
  const getProgressColor = () => {
    const colors = ['#4caf50', '#2196f3', '#ff9800', '#9c27b0', '#e91e63']; // Зеленый, Синий, Оранжевый, Фиолетовый, Розовый
    return colors[(level - 1) % colors.length];
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      height: '100vh',
      textAlign: 'center',
      color: 'inherit',
      paddingBottom: '80px',
      fontFamily: "'Chakra Petch', sans-serif"
    }}>
      {/* Верхняя часть */}
      <div style={{
        width: '100%',
        height: headerExpand && !isExiting ? 'calc(65vh + 120px)' : '120px',
        boxShadow: '0 1px 50px rgba(93, 95, 98, 0.6)',
        backgroundColor: 'rgba(168, 8%, 91%, 0.76)',
        borderBottomLeftRadius: '50% 20%',
        borderBottomRightRadius: '50% 20%',
        position: 'absolute',
        top: 0,
        left: 0,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
        transition: 'height 0.75s ease-in-out',
        flexDirection: 'column' // Размещаем элементы вертикально
      }}>
        <div style={{
          position: 'absolute',
          width: '200%',
          height: '200%',
          background: "url('https://www.transparenttextures.com/patterns/stardust.png') repeat",
          animation: 'level-moveBackground 20s linear infinite'
        }}></div>

        {/* Текущий уровень */}
        <h1 style={{
          fontFamily: "'Audiowide', sans-serif",
          color: '#fff',
          fontSize: '24px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0, // Плавное появление
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out' // Без анимации при выходе
        }}>
          {level} LEVEL
        </h1>

        {/* Полоска прогресса */}
        <div style={{
          position: 'relative',
          width: '56%',
          height: '15px',
          margin: '10px auto',
          backgroundColor: 'rgba(221, 221, 221, 0.1)',
          borderRadius: '10px',
          overflow: 'hidden',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0, // Плавное появление
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out' // Без анимации при выходе
        }}>
          <div style={{
            height: '100%',
            width: `${progress}%`,
            backgroundColor: getProgressColor(),
            transition: 'width 0.5s ease',
          }}>
            {[...Array(5)].map((_, i) => (
              <div key={i} style={{
                position: 'absolute',
                height: '100%',
                width: '2px',
                backgroundColor: 'rgba(221, 221, 221, 0.)',
                left: `${(i + 1) * 20}%`,
                top: 0
              }} />
            ))}
          </div>
        </div>

        {/* Кнопка для увеличения прогресса */}
        <button onClick={handleIncreaseProgress} style={{
          marginTop: '10px',
          backgroundColor: getProgressColor(),
          color: '#fff',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          fontSize: '14px',
          zIndex: '2',
          padding: '10px 20px',
          opacity: elementsVisible && !isExiting ? 1 : 0, // Плавное появление
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out' // Без анимации при выходе
        }}>
          Increase Progress
        </button>

        {/* Независимые блоки */}
        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',

          opacity: elementsVisible && !isExiting ? 1 : 0, // Плавное появление
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s' // Без анимации при выходе
        }}>
          <span>{leftTexts[0]}</span>
          <span>{rightTexts[0]}</span>
        </div>

        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',

          opacity: elementsVisible && !isExiting ? 1 : 0, // Плавное появление
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s' // Без анимации при выходе
        }}>
          <span>{leftTexts[1]}</span>
          <span>{rightTexts[1]}</span>
        </div>

        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',

          opacity: elementsVisible && !isExiting ? 1 : 0, // Плавное появление
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s' // Без анимации при выходе
        }}>
          <span>{leftTexts[2]}</span>
          <span>{rightTexts[2]}</span>
        </div>
      </div>

      {/* Добавление анимации через тег style */}
      <style>{`
        @keyframes level-moveBackground {
          0% { background-position: 0 0; }
          100% { background-position: -1000px -1000px; }
