import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LevelPage = () => {
  const [balance, setBalance] = useState(0); // Баланс пользователя
  const [balanceRate, setBalanceRate] = useState(1); // BalanceRate3
  const [level, setLevel] = useState(1);
  const [progress, setProgress] = useState(0);
  const [isExiting, setIsExiting] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false); // Для дизейбла кнопки
  const navigate = useNavigate();

  const balanceThresholds = [5, 10, 20, 40, 80, 160, 320, 640, 1280, 2560, 5120, 10240, 20480, 40960, 81920, 163840]; // Пороги баланса
  const rateMax = 15; // Максимальный balanceRate3

  useEffect(() => {
    const getInitialData = () => {
      window.Telegram.WebApp.CloudStorage.getItems(['balance', 'balanceRate3', 'level3', 'progress3'], (error, result) => {
        if (error) {
          console.error('Failed to get initial data from cloud storage:', error);
        } else {
          const initialBalance = result.balance ? parseInt(result.balance, 10) : 0;
          const initialBalanceRate = result.balanceRate3 ? parseInt(result.balanceRate3, 10) : 1;
          const initialLevel = result.level3 ? parseInt(result.level3, 10) : 1;
          const initialProgress = result.progress3 ? parseInt(result.progress3, 10) : 0;

          setBalance(initialBalance);
          setBalanceRate(initialBalanceRate);
          setLevel(initialLevel);
          setProgress(initialProgress);

          // Проверка на блокировку кнопки
          checkButtonStatus(initialBalance, initialBalanceRate);
        }
      });
    };

    getInitialData();
  }, []);

  const checkButtonStatus = (balance, balanceRate) => {
    const currentThreshold = balanceThresholds[balanceRate - 1];
    setIsButtonDisabled(balance < currentThreshold);
  };

  const handleIncreaseProgress = () => {
    const currentThreshold = balanceThresholds[balanceRate - 1];

    if (balance >= currentThreshold) {
      if (progress >= 100) {
        // Повышение уровня
        setLevel(prev => prev + 1);
        setProgress(0);

        // Увеличиваем balanceRate и проверяем порог
        if (balanceRate < rateMax) {
          setBalanceRate(prev => prev + 1);
        }

        // Сохраняем в CloudStorage
        window.Telegram.WebApp.CloudStorage.setItem('level3', (level + 1).toString(), (error) => {
          if (error) console.error('Failed to update level in cloud storage:', error);
        });
        window.Telegram.WebApp.CloudStorage.setItem('progress3', '0', (error) => {
          if (error) console.error('Failed to reset progress in cloud storage:', error);
        });
        window.Telegram.WebApp.CloudStorage.setItem('balanceRate3', (balanceRate + 1).toString(), (error) => {
          if (error) console.error('Failed to update balanceRate3 in cloud storage:', error);
        });
      } else {
        // Увеличиваем прогресс
        setProgress(prev => prev + 20);
        window.Telegram.WebApp.CloudStorage.setItem('progress3', (progress + 20).toString(), (error) => {
          if (error) console.error('Failed to update progress in cloud storage:', error);
        });
      }
    }

    // Проверка на блокировку кнопки после изменения баланса или прогресса
    checkButtonStatus(balance, balanceRate);
  };

  const getProgressColor = () => {
    const colors = [
      '#2196f3', '#4caf50', '#b39ddb', '#ff9800', '#ff8a80', '#cd7f32', '#c0c0c0', '#ffd700', '#e5e4e2', '#b9f2ff'
    ];
    return colors[(level - 1) % colors.length];
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
      height: '100vh',
      textAlign: 'center',
      color: 'inherit',
      paddingBottom: '80px',
      fontFamily: "'Chakra Petch', sans-serif"
    }}>
      <div style={{
        width: '100%',
        height: headerExpand && !isExiting ? 'calc(65vh + 120px)' : '120px',
        boxShadow: '0 1px 50px rgba(93, 95, 98, 0.6)',
        backgroundColor: 'rgba(168, 8%, 91%, 0.76)',
        borderBottomLeftRadius: '50% 20%',
        borderBottomRightRadius: '50% 20%',
        position: 'absolute',
        top: 0,
        left: 0,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
        transition: 'height 0.75s ease-in-out',
        flexDirection: 'column'
      }}>
        <div style={{
          position: 'absolute',
          width: '200%',
          height: '200%',
          background: "url('https://www.transparenttextures.com/patterns/stardust.png') repeat",
          animation: 'level-moveBackground 20s linear infinite'
        }}></div>

        <h1 style={{
          fontFamily: "'Audiowide', sans-serif",
          color: '#fff',
          fontSize: '24px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'
        }}>
          {level} LEVEL
        </h1>

        <div style={{
          position: 'relative',
          width: '56%',
          height: '15px',
          margin: '10px auto',
          backgroundColor: 'rgba(221, 221, 221, 0.1)',
          borderRadius: '10px',
          overflow: 'hidden',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out'
        }}>
          <div style={{
            height: '100%',
            width: `${progress}%`,
            backgroundColor: getProgressColor(),
            transition: 'width 0.5s ease',
          }}>
            {[...Array(5)].map((_, i) => (
              <div key={i} style={{
                position: 'absolute',
                height: '100%',
                width: '2px',
                backgroundColor: 'rgba(221, 221, 221, 0.)',
                left: `${(i + 1) * 20}%`,
                top: 0
              }} />
            ))}
          </div>
        </div>

        {/* Независимый блок с обработчиком клика для увеличения прогресса */}
        <div onClick={handleIncreaseProgress} style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          cursor: 'pointer',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s'
        }}>
          <span>{leftTexts[0]}</span>
          <span>{rightTexts[0]}</span>
        </div>

        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s'
        }}>
          <span>{leftTexts[1]}</span>
          <span>{rightTexts[1]}</span>
        </div>

        <div style={{
          marginTop: "20px",
          backgroundColor: 'gray',
          borderRadius: '25px',
          height: '50px',
          width: '70%',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 20px',
          zIndex: '2',
          opacity: elementsVisible && !isExiting ? 1 : 0,
          transition: isExiting ? 'opacity 0.5s ease-in-out' : 'opacity 0.5s ease-in-out 0.1s'
        }}>
          <span>{leftTexts[2]}</span>
          <span>{rightTexts[2]}</span>
        </div>
      </div>

      <style>{`
        @keyframes level-moveBackground {
          0% { background-position: 0 0; }
          100% { background-position: -1000px -1000px; }
        }
      `}</style>
    </div>
  );
};

export default LevelPage;
