import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
  const [currentValue, setCurrentValue] = useState(0);
  const [currentValueAxios, setCurrentValueAxios] = useState(0);
  const [targetValue, setTargetValue] = useState(0);
  const [baseValue, setBaseValue] = useState(0); // Базовая цена из API
  const [dataPoints, setDataPoints] = useState([]);
  const [centerValue, setCenterValue] = useState(0); // Центр шкалы
  const [range, setRange] = useState(1000); // Полный диапазон шкалы
  const canvasRef = useRef(null);

  // Функция для получения цены биткоина
  const fetchBitcoinPrice = async () => {
    try {
      const response = await fetch('https://api.coindesk.com/v1/bpi/currentprice/BTC.json');
      const data = await response.json();
      const price = data.bpi.USD.rate_float;
      setCurrentValueAxios(price)
      return Math.round(price * 100) / 100; // Округляем до двух знаков после запятой
    } catch (error) {
      console.error('Ошибка при получении цены биткоина:', error);
      return currentValue; // Возвращаем текущее значение при ошибке
    }
  };

  // Функция для генерации случайного изменения цены
  const getRandomPriceChange = () => {
    const change = Math.floor(Math.random() * (2330 - 500 + 1)) + 500; // Изменение в центах
    return Math.random() > 0.5 ? change / 100 : -change / 100; // Возвращаем изменение в долларах
  };

  // Функция для обновления целевой цены
  const updateTargetValue = async () => {
    const currentPrice = await fetchBitcoinPrice();
    const priceChange = getRandomPriceChange();
    setTargetValue(currentPrice + priceChange);
  };

  useEffect(() => {
    const updatePrice = async () => {
      const initialPrice = await fetchBitcoinPrice();
      setCurrentValue(initialPrice);
      setBaseValue(initialPrice);
      setCenterValue(initialPrice); // Изначально центр шкалы равен текущей цене
      setTargetValue(initialPrice);
    };

    updatePrice();
  }, []);

  useEffect(() => {
    const priceDifference = Math.abs(targetValue - currentValue);
    const intervalDuration = priceDifference > 0 ? 2000 / priceDifference : 100;

    const interval = setInterval(() => {
      setCurrentValue(prevValue => {
        if (prevValue < targetValue) {
          return prevValue + 0.01; // Увеличиваем на 1 цент
        } else if (prevValue > targetValue) {
          return prevValue - 0.01; // Уменьшаем на 1 цент
        } else {
          setDataPoints(prev => [...prev, prevValue]);
          updateTargetValue(); // Обновляем цель при достижении текущей цены
          return prevValue;
        }
      });
    }, 1);

    return () => clearInterval(interval);
  }, [currentValue, targetValue]);

  useEffect(() => {
    if (currentValue < centerValue - range / 2 || currentValue > centerValue + range / 2) {
      setCenterValue(currentValue);
    }
  }, [currentValue, centerValue, range]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const scale = window.devicePixelRatio || 1;
    canvas.width = 500 * scale;
    canvas.height = 300 * scale;
    ctx.scale(scale, scale);

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (dataPoints.length > 1) {
      ctx.strokeStyle = '#FFD700';
      ctx.lineWidth = 1.5;
      ctx.beginPath();

      dataPoints.forEach((value, index) => {
        const x = (canvas.width / scale / 2) + index * 10 - dataPoints.length * 10;
        const y = canvas.height / scale - ((value - (centerValue - range / 2)) / range) * canvas.height / scale;
        if (index === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }
      });

      ctx.stroke();

      const fixedY = canvas.height / scale - ((currentValue - (centerValue - range / 2)) / range) * canvas.height / scale;
      ctx.strokeStyle = 'lightblue';
      ctx.lineWidth = 1;
      ctx.beginPath();
      ctx.moveTo(0, fixedY);
      ctx.lineTo(canvas.width / scale, fixedY);
      ctx.stroke();

      ctx.font = '12px Arial';
      ctx.fillStyle = 'lightblue';
      ctx.fillText(`${currentValue.toFixed(2)} BTC`, canvas.width / scale - 150, fixedY - 10);
    }
  }, [dataPoints, centerValue, range, currentValue]);

  const handleFixValue = () => {
    setCenterValue(currentValue); // Перемещаем центр шкалы на текущее значение
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ marginBottom: '20px', fontSize: '18px', fontWeight: 'bold' }}>
        Текущая цена биткоина: ${currentValueAxios.toFixed(2)}
      </div>
      <canvas
        ref={canvasRef}
        style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
      ></canvas>
      <button
        onClick={handleFixValue}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px'
        }}>
        Зафиксировать текущее значение
      </button>
    </div>
  );
};

export default Page4;
