import React, { useEffect, useState } from 'react';
import './LevelPage.css';

const LevelPage = () => {
  const [headerExpand, setHeaderExpand] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const cards = ['Card 1', 'Card 2', 'Card 3', 'Card 4', 'Card 5'];

  useEffect(() => {
    setTimeout(() => {
      setHeaderExpand(true);
    }, 100);
  }, []);

  const nextCard = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % cards.length);
  };

  const prevCard = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? cards.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="level-home">
      <div className={`level-header ${headerExpand ? 'level-header-expand' : ''}`}>
        <div className="level-stars"></div>
        <h1>Level Page</h1>
      </div>

      <div className="carousel-container">
        <button className="carousel-button left" onClick={prevCard}>
          ‹
        </button>
        <div className="carousel">
          {cards.map((card, index) => (
            <div
              key={index}
              className={`carousel-card ${
                index === currentIndex
                  ? 'active'
                  : index === (currentIndex + 1) % cards.length
                  ? 'next'
                  : index === (currentIndex - 1 + cards.length) % cards.length
                  ? 'prev'
                  : ''
              }`}
            >
              {card}
            </div>
          ))}
        </div>
        <button className="carousel-button right" onClick={nextCard}>
          ›
        </button>
      </div>
    </div>
  );
};

export default LevelPage;
