import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
    const values = [
        78, 45, 92, 110, 67, 84, 56, 90, 102, 48, 88, 61, 70, 109, 81, 52, 94, 66, 75, 118,
        // ... другие значения
    ];

    const [currentIndex, setCurrentIndex] = useState(0);
    const [currentValue, setCurrentValue] = useState(values[0]);
    const [dataPoints, setDataPoints] = useState([]);
    const [fixedValue, setFixedValue] = useState(null);
    const canvasRef = useRef(null);

    useEffect(() => {
        if (currentIndex < values.length - 1) {
            const targetValue = values[currentIndex + 1];
            const difference = Math.abs(targetValue - currentValue);
            const duration = 1000; // Длительность анимации 1 секунда
            const stepTime = duration / difference;

            const interval = setInterval(() => {
                setCurrentValue(prevValue => {
                    if (prevValue < targetValue) {
                        return prevValue + 1;
                    } else if (prevValue > targetValue) {
                        return prevValue - 1;
                    } else {
                        setDataPoints(prev => [...prev, prevValue]);
                        setCurrentIndex(currentIndex + 1);
                        clearInterval(interval);
                        return prevValue;
                    }
                });
            }, stepTime);

            return () => clearInterval(interval);
        }
    }, [currentIndex, currentValue, values]);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        if (dataPoints.length > 1) {
            ctx.strokeStyle = fixedValue !== null ? 'lightblue' : '#FFD700';
            ctx.lineWidth = 3;
            ctx.beginPath();

            dataPoints.forEach((value, index) => {
                const x = (canvas.width / 2) + index * 10 - dataPoints.length * 10;
                const y = canvas.height - (value / 150) * canvas.height;
                if (index === 0) {
                    ctx.moveTo(x, y);
                } else {
                    ctx.lineTo(x, y);
                }
            });

            ctx.stroke();

            if (fixedValue !== null) {
                const fixedY = canvas.height - (fixedValue / 150) * canvas.height;
                ctx.strokeStyle = 'lightblue';
                ctx.lineWidth = 2;
                ctx.beginPath();
                ctx.moveTo(0, fixedY);
                ctx.lineTo(canvas.width, fixedY);
                ctx.stroke();
            }
        }
    }, [dataPoints, fixedValue]);

    const handleFixValue = () => {
        setFixedValue(currentValue);
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <canvas
                ref={canvasRef}
                width={500}
                height={300}
                style={{ border: '1px solid black', marginBottom: '20px' }}
            ></canvas>
            <button 
                onClick={handleFixValue} 
                style={{
                    padding: '10px 20px',
                    fontSize: '16px',
                    cursor: 'pointer',
                    backgroundColor: '#4CAF50',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px'
                }}>
                Зафиксировать текущее значение
            </button>
        </div>
    );
};

export default Page4;
