import React, { useState, useEffect, useRef } from 'react';

const Page4 = () => {
    const [previousValue, setPreviousValue] = useState(56000);
    const [currentValue, setCurrentValue] = useState(56000);
    const [rectangles, setRectangles] = useState([]);
    const [lastY, setLastY] = useState(null);  // Хранение последней позиции Y
    const canvasRef = useRef(null);
    const animationTimeoutRef = useRef(null);

    const generateValue = () => {
        const newValue = Math.floor(Math.random() * (56745 - 55255 + 1)) + 55255;
        setCurrentValue(newValue);
    };

    const generateRandomMultiplier = () => {
        const rangeSelector = Math.random();

        if (rangeSelector <= 0.6) {
            // 60% вероятность на диапазон от 0.01 до 0.1
            return Math.random() * (0.1 - 0.01) + 0.01;
        } else if (rangeSelector <= 0.8) {
            // 20% вероятность на диапазон от 0.11 до 0.5
            return Math.random() * (0.5 - 0.11) + 0.11;
        } else if (rangeSelector <= 0.9) {
            // 10% вероятность на диапазон от 0.51 до 0.9
            return Math.random() * (0.9 - 0.51) + 0.51;
        } else {
            // 10% вероятность на диапазон от 0.91 до 1
            return Math.random() * (1 - 0.91) + 0.91;
        }
    };

    useEffect(() => {
        if (currentValue !== previousValue) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');

            const scale = window.devicePixelRatio || 1;
            canvas.width = 500 * scale;
            canvas.height = 300 * scale;
            ctx.scale(scale, scale);

            const difference = currentValue - previousValue;
            let step = difference > 0 ? 1 : -1;
            let progress = 0;

            const startY = lastY !== null ? lastY : 150 - ((previousValue - 56000) / 1490) * 120;
            const targetY = 150 - ((currentValue - 56000) / 1490) * 120;
            const rectWidth = 20;

            let animationDirection = 1; // 1 для увеличения, -1 для уменьшения
            const startTime = Date.now();

            const interval = setInterval(() => {
                // Множитель для изменения шага на каждой итерации
                const randomMultiplier = generateRandomMultiplier();
                progress += step * animationDirection * randomMultiplier;

                ctx.clearRect(0, 0, canvas.width / scale, canvas.height / scale);

                const updatedRectangles = rectangles.map((rect) => ({
                    ...rect,
                    x: rect.x - rectWidth,
                }));

                updatedRectangles.forEach((rect) => {
                    ctx.fillStyle = rect.color;
                    ctx.fillRect(rect.x, rect.y, rect.width, rect.height);
                });

                const height = Math.abs(progress);
                let rectY;

                if (difference > 0) {
                    // Если значение увеличивается, рисуем сверху вниз
                    rectY = startY - height;
                } else {
                    // Если значение уменьшается, рисуем снизу вверх
                    rectY = startY;
                }

                ctx.fillStyle = difference > 0 ? 'green' : 'red';
                ctx.fillRect(canvas.width / scale / 2 - 15, rectY, rectWidth, height);

                if (Math.abs(progress) >= Math.abs(targetY - startY)) {
                    animationDirection *= -1; // Инвертируем направление
                }

                if (Date.now() - startTime >= 5000) { // 5 секунд прошли
                    clearInterval(interval);

                    updatedRectangles.push({
                        x: canvas.width / scale / 2 - 15,
                        y: rectY,
                        width: rectWidth,
                        height: height,
                        color: difference > 0 ? 'green' : 'red',
                    });

                    setRectangles(updatedRectangles);
                    setLastY(difference > 0 ? rectY : rectY + height);  // Сохраняем последнюю позицию Y в зависимости от направления
                    setPreviousValue(currentValue);
                }
            }, 20);

            animationTimeoutRef.current = interval;

            return () => clearInterval(interval);
        }
    }, [currentValue, previousValue, rectangles, lastY]);

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <canvas
                ref={canvasRef}
                style={{ width: 500, height: 300, border: '1px solid black', marginBottom: '20px' }}
            ></canvas>
            <button
                onClick={generateValue}
                style={{
                    padding: '10px 20px',
                    fontSize: '16px',
                    cursor: 'pointer',
                    backgroundColor: '#4CAF50',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px'
                }}>
                Generate Value
            </button>
        </div>
    );
};

export default Page4;
